import React, { useState, useEffect } from 'react';
import { Routes, Route, Link, useLocation } from 'react-router-dom';
import { workoutAPI, dietAPI, membershipAPI, contactAPI, authAPI } from '../utils/api';
import { FaDumbbell, FaAppleAlt, FaCrown, FaEnvelope, FaUsers, FaHome, FaPlus, FaEdit, FaTrash, FaEye, FaChartLine } from 'react-icons/fa';
import { toast } from 'react-toastify';

const AdminDashboard = () => {
  return (
    <div className="min-h-screen bg-gray-50">
      <div className="flex">
        {/* Sidebar */}
        <AdminSidebar />
        
        {/* Main Content */}
        <div className="flex-1 p-8">
          <Routes>
            <Route path="/" element={<AdminOverview />} />
            <Route path="/workouts" element={<ManageWorkouts />} />
            <Route path="/diets" element={<ManageDiets />} />
            <Route path="/memberships" element={<ManageMemberships />} />
            <Route path="/contacts" element={<ManageContacts />} />
            <Route path="/users" element={<ManageUsers />} />
          </Routes>
        </div>
      </div>
    </div>
  );
};

// Sidebar Component
const AdminSidebar = () => {
  const location = useLocation();
  
  const menuItems = [
    { path: '/admin', icon: <FaHome />, label: 'Dashboard', exact: true },
    { path: '/admin/workouts', icon: <FaDumbbell />, label: 'Workouts' },
    { path: '/admin/diets', icon: <FaAppleAlt />, label: 'Diet Plans' },
    { path: '/admin/memberships', icon: <FaCrown />, label: 'Memberships' },
    { path: '/admin/contacts', icon: <FaEnvelope />, label: 'Messages' },
    { path: '/admin/users', icon: <FaUsers />, label: 'Users' },
  ];

  const isActive = (path, exact) => {
    if (exact) {
      return location.pathname === path;
    }
    return location.pathname.startsWith(path);
  };

  return (
    <div className="w-64 bg-white shadow-lg min-h-screen">
      <div className="p-6 bg-gradient-to-br from-primary-600 to-secondary-600 text-white">
        <h2 className="text-2xl font-bold">Admin Panel</h2>
        <p className="text-sm opacity-90">FitLife Gym</p>
      </div>
      
      <nav className="p-4">
        {menuItems.map((item) => (
          <Link
            key={item.path}
            to={item.path}
            className={`flex items-center space-x-3 px-4 py-3 rounded-lg mb-2 transition-all ${
              isActive(item.path, item.exact)
                ? 'bg-primary-500 text-white shadow-md'
                : 'text-gray-700 hover:bg-gray-100'
            }`}
          >
            <span className="text-xl">{item.icon}</span>
            <span className="font-medium">{item.label}</span>
          </Link>
        ))}
      </nav>
    </div>
  );
};

// Overview Component
const AdminOverview = () => {
  const [stats, setStats] = useState({
    workouts: 0,
    diets: 0,
    users: 0,
    contacts: 0,
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const [workouts, diets, users, contacts] = await Promise.all([
        workoutAPI.getAll(),
        dietAPI.getAll(),
        authAPI.getUsers(),
        contactAPI.getAll(),
      ]);

      setStats({
        workouts: workouts.data.length,
        diets: diets.data.length,
        users: users.data.length,
        contacts: contacts.data.length,
      });
    } catch (error) {
      console.error('Failed to fetch stats:', error);
    } finally {
      setLoading(false);
    }
  };

  const statCards = [
    { label: 'Total Workouts', value: stats.workouts, icon: <FaDumbbell />, color: 'from-blue-500 to-blue-600' },
    { label: 'Diet Plans', value: stats.diets, icon: <FaAppleAlt />, color: 'from-green-500 to-green-600' },
    { label: 'Total Users', value: stats.users, icon: <FaUsers />, color: 'from-purple-500 to-purple-600' },
    { label: 'Messages', value: stats.contacts, icon: <FaEnvelope />, color: 'from-orange-500 to-orange-600' },
  ];

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div>
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-800">Dashboard Overview</h1>
        <p className="text-gray-600 mt-2">Welcome back! Here's what's happening with FitLife Gym.</p>
      </div>

      {/* Stats Grid */}
      <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        {statCards.map((stat, index) => (
          <div key={index} className={`card p-6 bg-gradient-to-br ${stat.color} text-white`}>
            <div className="flex items-center justify-between mb-4">
              <div className="text-4xl opacity-80">{stat.icon}</div>
              <div className="text-3xl font-bold">{stat.value}</div>
            </div>
            <p className="text-sm font-medium opacity-90">{stat.label}</p>
          </div>
        ))}
      </div>

      {/* Quick Actions */}
      <div className="card p-6 mb-8">
        <h2 className="text-xl font-bold text-gray-800 mb-4">Quick Actions</h2>
        <div className="grid md:grid-cols-3 gap-4">
          <Link to="/admin/workouts" className="btn-primary text-center">
            <FaPlus className="inline mr-2" />
            Add Workout
          </Link>
          <Link to="/admin/diets" className="btn-secondary text-center">
            <FaPlus className="inline mr-2" />
            Add Diet Plan
          </Link>
          <Link to="/admin/memberships" className="bg-gradient-to-r from-yellow-500 to-orange-500 text-white px-6 py-3 rounded-lg font-semibold hover:shadow-lg transition-all text-center">
            <FaPlus className="inline mr-2" />
            Add Membership
          </Link>
        </div>
      </div>

